mapper.update:
update SC0201 set STATUS = 0, DELETE_FLAG = '1', UPDATE_TIME = now(), UPDATE_USER = #{ss01Id,jdbcType=VARCHAR}
where PROJECT_NO = #{projectNo,jdbcType=VARCHAR}

    and SC0201_ID not in

        #{id,jdbcType=VARCHAR}

mapper.select:
select SC0201_ID from SC0201 where PROJECT_NO = #{projectNo,jdbcType=VARCHAR}

    and SC0201_ID not in

        #{id,jdbcType=VARCHAR}

select t1.*, sum(t2.SUPER_P_SCORE) as totalSuperScore, sum(t2.SCORE) as totalScore
from SC0201 t1
left join SC0201 t2
on t2.SUPER_P_NO regexp concat('^', t1.SUPER_P_NO) and t2.STATUS = '1' and t2.DELETE_FLAG = '0' and
t2.PROJECT_NO = #{projectNo,jdbcType=VARCHAR}
where t1.SUPER_P_LEVEL = #{level,jdbcType=VARCHAR}
and t1.PROJECT_NO = #{projectNo,jdbcType=VARCHAR}

    and t1.SUPER_P_NO regexp concat('^', #{code,jdbcType=VARCHAR})

group by t1.SC0201_ID
order by cast(REPLACE(t1.SYS_NO, '.', '') as SIGNED)
