mapper.select:
select t1.SC01_ID, t1.SUPER_P_NO, t1.SYS_NO, t1.SUPER_P_CHECK, t1.SUPER_P_EXP, t1.SUPER_P_SCORE, t1.SUPER_P_LEVEL, t1.IF_ADD, t1.STATUS, SUM(t2.SUPER_P_SCORE) as TOTAL_SCORE from SC01 t1 LEFT JOIN SC01 t2 ON t2.SUPER_P_NO REGEXP CONCAT('^', t1.SUPER_P_NO) AND t2.STATUS = '1' AND t2.DELETE_FLAG = '0' where t1.DELETE_FLAG = '0'
and t1.SUPER_P_NO like concat('%', #{query.superPNo,jdbcType=VARCHAR}, '%')
and t1.SUPER_P_CHECK like concat('%', #{query.superPCheck,jdbcType=VARCHAR}, '%')
and t1.STATUS = #{query.status,jdbcType=VARCHAR}
and t1.SUPER_P_LEVEL = #{query.superPLevel,jdbcType=VARCHAR}
GROUP BY t1.SC01_ID order by t1.SUPER_P_NO

select SUPER_P_NO from SC01 where SUPER_P_LEVEL = #{level,jdbcType=VARCHAR}

  and SUPER_P_NO like concat('', #{code,jdbcType=VARCHAR}, '%')

order by SUPER_P_NO desc limit 1

SELECT t1.*, SUM(t2.SUPER_P_SCORE) as TOTAL_SCORE FROM SC01 t1 LEFT JOIN SC01 t2 ON t2.SUPER_P_NO REGEXP CONCAT('^', t1.SUPER_P_NO) AND t2.STATUS = '1' AND t2.DELETE_FLAG = '0' WHERE t1.SUPER_P_LEVEL = 1
and t1.SUPER_P_NO like concat('%', #{query.superPNo,jdbcType=VARCHAR}, '%')
and t1.SUPER_P_CHECK like concat('%', #{query.superPCheck,jdbcType=VARCHAR}, '%')
and t1.STATUS = #{query.status,jdbcType=VARCHAR}
and t1.SUPER_P_LEVEL = #{query.superPLevel,jdbcType=VARCHAR}
GROUP BY t1.SC01_ID order by cast(REPLACE(t1.SYS_NO, '.', '') as SIGNED)
